import React, { useState, useContext, useEffect } from 'react';
import { GlobalContext } from "./GlobalState";
import { Link, useHistory } from "react-router-dom";
import axios from 'axios';
import {
    Form,FormGroup,Input,Button,Label
} from 'reactstrap'

export const EditVendor = (props) => {
  

    
    const { editUser, users } = useContext(GlobalContext);
    const [selectedUser, setSelectedUser] = useState({
      id: '',
      vendorName  : '',
      title:'' ,
      contactEmail: '' ,
      websiteURL: ''
    })
    const history = useHistory();
    const currentUserId = props.match.params.id;
  
    useEffect(() => {
      const userId = currentUserId;
     // const selectedUser = users.find(user => user.id === userId);

     axios.get("/api/:"+userId)
      .then(response=>{
       
         var data = response.data;
         setSelectedUser(data);
        console.log(data);
        console.log("Single Data received..",data);
      })
      .catch((error)=>{
        console.log(error.response);
      });
      
    }, [currentUserId, users])
  
    const onChange = (e) => {
      setSelectedUser({ ...selectedUser, [e.target.name]: e.target.value })
    }
  
    const onSubmit = (e) => {
      e.preventDefault();
      editUser(selectedUser);
      history.push("/")
    }

    return (
        <div>
           <Form onSubmit={onSubmit}>
                <FormGroup>
                <Label>Vendor Name</Label>
                <Input type="text" value={selectedUser.vendorName} onChange={onChange} name="vendorName" placeholder="Enter user" required></Input>
                </FormGroup>
                <FormGroup>
                <Label>Type</Label>
                <Input type="text" value={selectedUser.title} onChange={onChange} name="title" placeholder="Enter user" required></Input>
                </FormGroup>
                <FormGroup>
                <Label>Contact Email</Label>
                <Input type="text" value={selectedUser.contactEmail} onChange={onChange} name="contactEmail" placeholder="Enter user" required></Input>
                </FormGroup>
                <FormGroup>
                <Label>Website URL</Label>
                <Input type="text" value={selectedUser.websiteURL} onChange={onChange} name="websiteURL" placeholder="Enter user" required></Input>
                </FormGroup>
                <Button type="submit">Submit</Button>
                <Link to="/" className="btn btn-danger ml-2 ">cancel</Link>
            </Form>
        </div>
    )
}
