//eslint-disable-next-line
import React ,{createContext,useReducer} from 'react';



const initialState = { users:[]
};

const AppReducer = (state,action) => {
    switch(action.type){

      case 'removeUser' :
        return {
          users : state.users.filter(user => {
            return user.id !==action.userID
          })
        }

        case 'addUser':
          
          return {
           
            ...state,
            users: [action.userID, ...state.users]
          }
        case 'editUser':
          const updateUser = action.userID;
    
          const updateUsers = state.users.map(user => {
            if (user.id === updateUser.id) {
              return updateUser;
            }
            return user;
          })
          return {
            ...state,
            users: updateUsers
          }
    


        default :
        return state;
    }
}

// Create Context
export const GlobalContext = createContext(initialState);

// Provider Component
export const GlobalProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);
   
  const removeUser =(id) =>{
    dispatch({
      type:'removeUser',
      userID :id
    })
  }

  const addUser = (user) => {
    dispatch({
      type: 'addUser',
      userID: user
    })

   // console.log(user);
  }

  const editUser = (user) => {
    dispatch({
      type: 'editUser',
      userID: user
    })
  }
    return (
        <GlobalContext.Provider value={{
          users: state.users,
          removeUser,
          addUser,
          editUser
          
        }}>
          {children}
        </GlobalContext.Provider>
      )
}
