//eslint-disable-next-line
import React ,{createContext,useReducer} from 'react';



const initialState = { users:[
        {id:1, vendorName  : 'User One', title:'some Title' ,contactEmail: 'contact@example.com' ,websiteURL: 'abc1.com'},
        {id:2, vendorName  : 'User Two', title:'some Title' ,contactEmail: 'contact2@example.com' ,websiteURL: 'abc2.com'},
        {id:3, vendorName  : 'User Three', title:'some Title' ,contactEmail: 'contact3@example.com' ,websiteURL: 'abc3.com'}
        
    ]
};

const AppReducer = (state,action) => {
    switch(action.type){

      case 'removeUser' :
        return {
          users : state.users.filter(user => {
            return user.id !==action.userID
          })
        }


        default :
        return state;
    }
}

// Create Context
export const GlobalContext = createContext(initialState);

// Provider Component
export const GlobalProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);
   
  const removeUser =(id) =>{
    dispatch({
      type:'removeUser',
      userID :id
    })
  }
    return (
        <GlobalContext.Provider value={{
          users: state.users,
          removeUser
          
        }}>
          {children}
        </GlobalContext.Provider>
      )
}
