// eslint-disable-next-line
import React,{useContext} from 'react';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import {Link} from 'react-router-dom';
import {GlobalContext} from './GlobalState'
import {
    ListGroup,ListGroupItem,Button
} from 'reactstrap'

const AllVendor = () => {

    const {users ,removeUser} = useContext(GlobalContext);
     console.log(users);
    

     return (
        <ListGroup className="mt-4">
          {users.length > 0 ? (
            <>
            <ListGroupItem className="d-flex">
            <span className="values name">vendorName</span><span className="values">Title</span><span className="values">contact Email</span><span className="values">websiteURL</span>
            </ListGroupItem>
            
              {users.map(user => (
                <ListGroupItem className="d-flex" key={user.id}>
                  <span className="values name">{user.vendorName}</span><span className="values">{user.title}</span><span className="values">{user.contactEmail}</span><span className="values">{user.websiteURL}</span>
                  <div className="ml-auto">
                    <Link to={`/edit/${user.id}`} color="warning" className="btn btn-warning mr-1"><EditIcon /></Link>
                    <Button  onClick={()=>removeUser(user.id)} color="danger"><DeleteIcon /></Button>   
                  </div>
                </ListGroupItem>
              ))}
            </>
          ) : (
              <h4 className="text-center">No Users</h4>
            )}
        </ListGroup>
      )

}

export default AllVendor
