import React, { useState, useContext } from 'react';
import { GlobalContext } from "./GlobalState";
import { v4 as uuid } from "uuid";
import { Link, useHistory } from "react-router-dom";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button
} from "reactstrap";

export const AddVendor = () => {
  const [data, setData] = useState({
      name: '',
      type: '',
      contactEmail:'',
      websiteURL: ''

  });
  const { addUser } = useContext(GlobalContext);
  const history = useHistory();

  const onSubmit = (e) => {
    e.preventDefault();
    const newUser = {
      id: uuid(),
      name: 'hi',
      type: 'type',
      contactEmail :'cv',
      websiteURL: 'we'
      
    }
    addUser(newUser);
    history.push("/");
  }

  const onChange = (e) => {
    setData([e.target.name]=e.target.value);
  }
    return (
        <div>
            <Form>
                <FormGroup>
                

                <Label>Vendor Name</Label>
                <Input type="text" value={data.name} onChange={onChange} name="name" placeholder="Name"></Input>
                </FormGroup>
                <FormGroup>
                <Label>Type</Label>
                <Input type="text" value={data.type} onChange={onChange} name="type" placeholder="Type"></Input>
                </FormGroup>
                <FormGroup>
                <Label>Contact Email</Label>
                <Input type="text" value={data.contactEmail} onChange={onChange} name="mail" placeholder="Email"></Input>
                </FormGroup>
                <FormGroup>
                <Label>Website URL</Label>
                <Input type="text" value={data.websiteURL} onChange={onChange} name="url" placeholder="Website URL"></Input>
                </FormGroup>
                <Button type="submit">Submit</Button>
                <Link to="/" className="btn btn-danger ml-2 ">cancel</Link>
            </Form>
        </div>
    )
}
