// eslint-disable-next-line
import React,{useContext} from 'react';
import {Link} from 'react-router-dom';
import {GlobalContext} from './GlobalState'
import {
    ListGroup,ListGroupItem,Button
} from 'reactstrap'

const AllVendor = () => {

    const {users} = useContext(GlobalContext);
     console.log(users);
    

     return (
        <ListGroup className="mt-4">
          {users.length > 0 ? (
            <>
              {users.map(user => (
                <ListGroupItem className="d-flex" key={user.id}>
                  <strong className="values">{user.vendorName}</strong><strong className="values">{user.title}</strong><strong className="values">{user.contactEmail}</strong><strong className="values">{user.websiteURL}</strong>
                  <div className="ml-auto">
                    <Link to={`/edit/${user.id}`} color="warning" className="btn btn-warning mr-1">Edit</Link>
                    <Button  color="danger">Delete</Button>   
                  </div>
                </ListGroupItem>
              ))}
            </>
          ) : (
              <h4 className="text-center">No Users</h4>
            )}
        </ListGroup>
      )

}

export default AllVendor
