// eslint-disable-next-line
import React,{useContext,useState,useEffect} from 'react';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import { Link, useHistory } from "react-router-dom";
import {GlobalContext} from './GlobalState'
import axios from 'axios';
import {
    ListGroup,ListGroupItem,Button
} from 'reactstrap'

const AllVendor = () => {

  const history = useHistory();
    const [vendorList,setVendorList] = useState({});
  

    useEffect(()=>{
      axios.get('/api')
      .then((response)=>{
        var data = response.data;
        setVendorList(data);
        console.log("Data received..");
      })
      .catch((error)=>{
        console.log(error);
      });
    },[])

    function removeUser(id){
      axios({
        url:'http://localhost:3000/api/delete',
        method:'POST',
        data:id
      })
      .then(()=>{
        console.log("Data deleted successfully");
      })
      .catch(()=>{
        console.log("Internel server error");
      });

      history.push("/");


    }

    
     
    

     return (
        <ListGroup className="mt-4">
          {vendorList.length > 0 ? (
            <>
            <ListGroupItem className="d-flex">
            <span className="values name">Vendor Name</span><span className="values">Title</span><span className="values">contact Email</span><span className="values">websiteURL</span>
            </ListGroupItem>
            
              {vendorList.map(vendor => (
                <ListGroupItem className="d-flex" key={vendor.id}>
                  <span className="values name">{vendor.vendorName}</span><span className="values">{vendor.title}</span><span className="values">{vendor.contactEmail}</span><span className="values">{vendor.websiteURL}</span>
                  <div className="ml-auto">
                    <Link to={`/edit/${vendor.id}`} color="warning" className="btn btn-warning mr-1"><EditIcon /></Link>
                    <Button  onClick={()=>removeUser(vendor.id)} color="danger"><DeleteIcon /></Button>   
                  </div>
                </ListGroupItem>
              ))}
            </>
          ) : (
              <h4 className="text-center">Loading...</h4>
            )}
        </ListGroup>
      )

}

export default AllVendor
